sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
// Add ccToEmployee		  
email.addAddress('cc',current.request_for.email + '');

// Add ccToITadmin
var itGroup = new GlideRecord('sys_user_group');
itGroup.addQuery('name','itAdmin');
itGroup.query();
if(itGroup.next()){
	var emilgroup = itGroup.getValue('email');
	gs.info('The IT Admin email:' + emailgroup); 
	if(emilgroup){
	email.addAddress("cc", emailgroup + '');
	}
}

})(current, template, email, email_action, event);
